(1, 'Metis Copy', 'metis_copy26', 'tabs', '.metis_copy26 .tp-tab-number {\n    color:##title-color##;\n    font-size:##title-size##px;\n    line-height:30px;\n    font-weight:##title-weight##;\n    font-family: \\\'##font-family##\\\';\n    width: ##basicwidth##px;   \n    display: inline-block;\n	position:absolute;\n    text-align:center;\n    box-sizing:border-box;\n  	height: 35px;\n}\n.metis_copy26 .tp-tab-number:after {\n    content: \\\"\\\";\n    display: block;\n    width: 2px;\n    height: 100%;\n    position: absolute;\n    top: 0;\n    left: 80%;\n    z-index: 3;\n    background: #fff;\n}\n.metis_copy26 .tp-tab-mask {\n   left:0px;\n   top:0px;\n   max-width:##basicwidth## !important;   \n   line-height:30px;\n   transition:0.4s padding-left, 0.4s left, 0.4s max-width;\n  overflow: visible !important;\n  max-width: 100% !important;\n}\n\n.metis_copy26:hover .tp-tab-mask{\n   padding-left:0px;\n   max-width:80px !important;\n}\n\n.metis_copy26 .tp-tab-divider { \n	border-right: 1px solid transparent;\n    height: 30px;\n    width: 1px;\n    display: inline-block;\n    position:absolute;\n    left:##basicwidth##px;\n    transition:0.4s all;\n}\n\n.metis_copy26 .tp-tab-title {\n    color:##desc-color##;\n    font-size:##desc-size##px;\n    line-height:##desc-size##px;\n    font-weight:##desc-font-weight##;\n    font-family: \\\'##font-family##\\\';\n    position:relative;\n    line-height:30px;\n    padding-left: 30px;\n    display: inline-block;\n    transform:translatex(-100%);\n    transition:0.4s all;\n}\n\n.metis_copy26 .tp-tab-title-mask {\n   position:absolute;\n   overflow:hidden;\n   left:##basicwidth##px; \n}\n\n.metis_copy26:hover .tp-tab-title {\n   transform:translatex(0);\n }\n\n.metis_copy26 .tp-tab { \n	opacity: 0.15;\n    transition:0.4s all;\n    width: auto !important;\n}\n\n.metis_copy26 .tp-tab:hover,\n.metis_copy26 .tp-tab.selected {\n    opacity: 1; \n}\n\n.metis_copy26 .tp-tab.selected .tp-tab-divider {\n	border-right: 1px solid #cdb0